import React, { Component } from 'react';
import './App.css';
//import Input from './Input'

class App extends Component {
  constructor(){
    super() 

      this.state = {
        taskList: [],
        task:''
      }
  }

  updateTaskList = (e) => {
    this.setState({
      task: e.target.value
    })
  }
  addTask = () => {
    this.setState({
      taskList: this.state.taskList.concat(this.state.task)
    })
  }



  render() {
    console.log(this.state.taskList)
    return (
      <div className="App">
       <div className="container">
    <h1 className="text-center">todos</h1>
      <Input tas/>

    <ul className="list-group">
      <Todo />

        <li className="list-group-item">
            <input type="checkbox"  value="on" />
            <label>write the content for the next module</label>
        </li>
        <li className="list-group-item">
            <input type="checkbox" value="on" />
            <label className="done">buy cheese</label>
        </li>
        <li class="list-group-item">
            <input type="checkbox" value="on" />
            <label className="done">buy milk</label>
        </li>
    </ul>

    <select>
        <option value="all">all</option>
        <option value="active">active</option>
        <option value="complete">complete</option>
    </select>

    <button className="pull-right btn btn-default">Clear Complete</button>
</div>
      </div>
    );
  }
}

class Input extends Component {
  render() {
      return(
          <form>
          <div className="input-group">
              <span className="input-group-btn">
                  <button className="btn btn-primary" type="submit">Add</button>
              </span>
              <input className="form-control" onChange={this.updateTaskList} value={this.props.task} placeholder="add a todo" />
          </div>
      </form>
      )
  }
}




class Todo extends Component {
  render() {

    return( 
      <li className="list-group-item">
            <input type="checkbox" value='on' />
            <label></label>
      </li>
    )
  }

}


export default App;
