import React, { Component } from 'react';
import './App.css';
import Input from './Input'

class App extends Component {
  constructor(){
    super() 

      this.state = {
        todoList: [],
        todo:'',
        active: true
      }
  }

  updateTaskList = (e) => {
    this.setState({
      todo: e.target.value
    })
  }
  addT = (e) => {
    e.preventDefault()
    this.setState({
      todoList: this.state.todoList.concat(this.state.todo)
    })
  }

  render() {
    console.log(this.state.todoList)
    return (
      <div className="App">
       <div className="container">
    <h1 className="text-center">todos</h1>
      <Input task = {this.state.todo}
      updateTaskList = {this.updateTodoList}
      addTask = {this.addTodo}/>

    <ul className="list-group">
      <Todo task={this.state.todo}
            active={this.state.active}/>

        <li className="list-group-item">
            <input type="checkbox"  value="on" />
            <label>write the content for the next module</label>
        </li>
        <li className="list-group-item">
            <input type="checkbox" value="on" />
            <label className="done">buy cheese</label>
        </li>
        <li className="list-group-item">
            <input type="checkbox" value="on" />
            <label className="done">buy milk</label>
        </li>
    </ul>

    <select>
        <option value="all">all</option>
        <option value="active">active</option>
        <option value="complete">complete</option>
    </select>

    <button className="pull-right btn btn-default">Clear Complete</button>
</div>
      </div>
    );
  }
}

