import React, { Component } from 'react';
import './App.css';
//import Input from './Input'

class App extends Component {
  constructor(){
    super() 

      this.state = {
        todoList: [],
        todo:'',
        active: true
      }
  }

  updateTodoList = (e) => {
    this.setState({
      todo: e.target.value
    })
  }
  addTodo = (e) => {
    e.preventDefault()
    this.setState({
      todoList: this.state.todoList.concat(this.state.todo)
    })
  }

  render() {
    console.log(this.state.todoList)
    return (
      <div className="App">
       <div className="container">
    <h1 className="text-center">todos</h1>
      <Input t = {this.state.todo}
      updateTaskList = {this.updateTodoList}
      addTask = {this.addTodo}/>

    {/* <ul className="list-group">
      <Todo task={this.state.todo}
            active={this.state.active}/>
        <li className="list-group-item">
            <input type="checkbox"  value="on" />
            <label>write the content for the next module</label>
        </li>
    </ul> */}

    <select>
        <option value="all">all</option>
        <option value="active">active</option>
        <option value="complete">complete</option>
    </select>

    <button className="pull-right btn btn-default">Clear Complete</button>
</div>
      </div>
    );
  }
}

class Input extends Component {
  render() {
      return(
          <form>
          <div className="input-group">
              <span className="input-group-btn">
                  <button className="btn btn-primary" type="submit" onClick={this.props.addTodo} >Add</button>
              </span>
              <input className="form-control" onChange={this.props.updateTodoList} value={this.props.todo} placeholder="add a todo" />
          </div>
      </form>
      )
  }
}




