import React, { Component } from 'react';
import './App.css';
import Input from './Input'

class App extends Component {
  render() {
    var tasks = this.props.tasks
    console.log(tasks)
    return (
      <div className="App">
       <div className="container">
    <h1 className="text-center">todos</h1>
      <Input />

    <ul className="list-group">
      <Todo />
      <Todo />
      <Todo />

        {/* <li className="list-group-item">
            <input type="checkbox" value="on" />
            <label>learn angular</label>
        </li> */}
        <li className="list-group-item">
            <input type="checkbox" value="on" />
            <label>write the content for the next module</label>
        </li>
        <li className="list-group-item">
            <input type="checkbox" value="on" />
            <label className="done">buy cheese</label>
        </li>
        <li class="list-group-item">
            <input type="checkbox" value="on" />
            <label className="done">buy milk</label>
        </li>
    </ul>

    <select>
        <option value="all">all</option>
        <option value="active">active</option>
        <option value="complete">complete</option>
    </select>

    <button className="pull-right btn btn-default">Clear Complete</button>
</div>
      </div>
    );
  }
}


class Todo extends Component {
  render() {

    return( 
      <li className="list-group-item">
            <input type="checkbox" value="on" />
            <label>{this.props.task.text}</label>
      </li>
    )
  }

}


export default App;
