import React, { Component } from 'react';
import './App.css';
import Input from './Input'

class App extends Component {
  constructor(){
    super() 

      this.state = {
        todoList: [],
        todo:'',
        id: 0,
        category: 'all',
        disabled: true

      }
  }

  updateTodoList = (e) => {
    this.setState({
      todo: e.target.value
    })
  }
  addTodo = (e) => {
    e.preventDefault()
    this.setState({
      todoList: [{name: this.state.todo, id: this.state.id, isComplete: false}].concat(this.state.todoList),
      todo: [],
      id: this.state.id + 1
    })
  }

  handleSubmit =(e) => {
    this.addTodo.Todo()
    this.updateTodoList()
  }

  handleChange = (id) => {
    let updatedList = this.state.todoList
   for (let i=0; i<updatedList.length; i++) {
     if(id === updatedList[i].id) {
       if(updatedList[i].isComplete === false) {
      updatedList[i].isComplete = true
     }
      else {
      updatedList[i].isComplete = false
     }
    }
  } 
   this.setState({
     todoList: updatedList
   })
  } 

  clearCompleted = () => {
    this.setState({
      todoList: this.state.todoList.filter(todo => !todo.isComplete)
    })
  }

  showStatus = (e) => {
    this.setState({
      category: e.target.value
    })  
  }

  changeDisabled = () => {
    this.setState()
  }

  render() {
    return (
      <div className="App">
       <div className="container">
    <h1 className="text-center">todos</h1>
      <Input todo = {this.state.todo}
      updateTodoList = {this.updateTodoList}
      addTodo = {this.addTodo}/>
      <TodoList todo={this.state.todo}
            todoList={this.state.todoList}
            handleChange={this.handleChange}
            category={this.state.category}
            disabled={this.state.disabled}
            />
    <select value={this.state.category} onChange={this.showStatus}>
        <option value="all">all</option>
        <option value="active">active</option>
        <option value="complete">complete</option>
    </select>
    <button className="pull-right btn btn-default" onClick={ this.clearCompleted } 
    disabled={ this.changeDisabled }
    >Clear Complete</button>
</div>
      </div>
    );
  }
}

class TodoList extends Component {
  render() {
    const { category } = this.props
    const { todoList } = this.props

    let newList = todoList.filter((todo) => {
    if(category === 'active') {
      return !todo.isComplete
    } 
    else if (category === 'complete') {
      return todo.isComplete
    } 
    else {
      return true
    }
     }
    )
     const finalTodoList=newList.map((newTodo, i) => {
     return <Todo todo={newTodo}
     handleChange={this.props.handleChange} key={i}
     disabled = {this.props.disabled}
     />
    })
    return(
      <ul className="list-group">{finalTodoList}</ul>
    ) 
}}

class Todo extends Component {
  render() {
    return( 
      <li className="list-group-item">
            <input type="checkbox" checked={this.props.todo.isComplete} onChange={()=> this.props.handleChange(this.props.todo.id)} 
            />
            <label className={ this.props.todo.isComplete ? 'done' : ''} >{this.props.todo.name}</label>
      </li>
    )
  }}

export default App;
