import React, { Component } from 'react';
import './App.css';
import Input from './Input'

class App extends Component {
  constructor(){
    super() 

      this.state = {
        todoList: [],
        todo:'',
        id: 0
      }
  }

  updateTodoList = (e) => {
    this.setState({
      todo: e.target.value
    })
  }
  addTodo = (e) => {
    e.preventDefault()
    this.setState({
      todoList: this.state.todoList.concat({name: this.state.todo, id: this.state.id, isComplete: false}),
      todo: [],
      id: this.state.id + 1
    })
  }

  handleSubmit =(e) => {
    this.addTodo.Todo()
    this.updateTodoList()
  }

  clearCompletedTodos = (e)=> {
    this.setState({
      todoList: []
    })
  }

  handleChange = (id) => {
    console.log(id)
    let updatedList = this.state.todoList
   for (let i=0; i<updatedList.length; i++) {
     if(id === updatedList[i].id) {
       if(updatedList[i].isComplete === false)
      updatedList[i].isComplete = true
     }
   }

   this.setState({
     todoList: updatedList
   })
    // let id = this.state.id +1
    // let isComplete = true
    // this.setState({
    //   isComplete: isComplete,
    //   id: id,
    // })
  }

  render() {
    console.log(this.state.todoList)
    return (
      <div className="App">
       <div className="container">
    <h1 className="text-center">todos</h1>
      <Input todo = {this.state.todo}
      updateTodoList = {this.updateTodoList}
      addTodo = {this.addTodo}/>
    <ul className="list-group">
      <TodoList todo={this.state.todo}
            todoList={this.state.todoList}
            handleChange={this.handleChange}
            />
        <li className="list-group-item">
            <input type="checkbox" value="on" />
            <label>write the content for the next module</label>
        </li>
    </ul>
    <select>
        <option value="all">all</option>
        <option value="active">active</option>
        <option value="complete">complete</option>
    </select>
    <button className="pull-right btn btn-default" onClick={this.clearCompletedTodos} >Clear Complete</button>
</div>
      </div>
    );
  }
}

class TodoList extends Component {
  render() {
     const todoList=this.props.todoList.map((newTodo) => {
     return <Todo todo={newTodo}
     handleChange={this.props.handleChange}

     />
    })
    return(
      <ul>{todoList}</ul>
    ) 
}}

class Todo extends Component {
  // handleChange = (id) => {
  //   console.log(id) //id of checkbox
  //  this.props.handleChange(this.props.id)
  // }
  render() {
    return( 
      <li className="list-group-item">
            <input type="checkbox" onChange={()=> this.props.handleChange(this.props.todo.id)} 
            // checked={this.props.??} 
            />
            <label>{this.props.todo.name}</label>
      </li>
    )
  }}


export default App;
