import React, { Component } from 'react';
import './App.css';
//import Input from './Input'

class App extends Component {
  constructor() {
    super()

    this.state ={
      statuses: [{
        text: 'Im sad',
        date: new Date().toString()
      },{
        text: 'Im happy',
        date: new Date().toString()
      }],
      statusText: ''
    }
  }

  getInput = (e) => {
    this.setState({
      statusText: e.target.value
    })
  }
  addStatus =(e) => {
    this.setState({
      statuses: [{
        text: this.state.statusText,
        date: new Date().toString()
      }].concat(this.state.statuses)
    })
  }

  deleteStatus = i => {
    this.state.statuses.splice(i, 1)
    this.setState({
      statuses: this.state.statuses
    })
  }

  render() {
    const statuses = this.state.statuses.map((status, i) => {
      return (
        <div style={{
          margin: '10px',
          border: '1px solid'
        }}>
          <div>{status.text}</div>
          <div>{status.date}</div>
          <button onClick={() =>this.deleteStatus(i)}>Delete</button>
        </div>
      )
    })
    return (
      <div className="App">
      <h1>Tod</h1>
      <input type='text' onChange={this.getInput} value={this.state.statusText}/>
      <button onClick={this.addStatus}>Post it!</button>
      {statuses}
      </div>
    );
  }
}



export default App;
