import React, { Component } from 'react';
import './App.css';
import Input from './Input'

class App extends Component {
  constructor(){
    super() 

      this.state = {
        todoList: [],
        todo:'',
        status: true,
        key: 1
      }
  }

  updateTodoList = (e) => {
    this.setState({
      todo: e.target.value
    })
  }
  addTodo = (e) => {
    e.preventDefault()
    this.setState({
      todoList: this.state.todoList.concat(this.state.todo),
      todo: []
    })
  }
  // changeStatus = () => {
  //   this.setState({
  //     status: false
  //   })
  // }

  handleSubmit = (e) => {
    this.state.addTodo()
    tnis.state.u
  }


  render() {
    console.log(this.state.todoList)
    return (
      <div className="App">
       <div className="container">
    <h1 className="text-center">todos</h1>
      <Input todo = {this.state.todo}
      updateTodoList = {this.updateTodoList}
      addTodo = {this.addTodo}/>

    <ul className="list-group">
      <TodoList todo={this.state.todo}
            todoList={this.state.todoList}
            active={this.state.active}/>
        <li className="list-group-item">
            <input type="checkbox"  value="on" />
            <label>write the content for the next module</label>
        </li>
    </ul>

    <select>
        <option value="all">all</option>
        <option value="active">active</option>
        <option value="complete">complete</option>
    </select>

    <button className="pull-right btn btn-default">Clear Complete</button>
</div>
      </div>
    );
  }
}

class TodoList extends Component {
  render() {
    const todoList=this.props.todoList.map((newTodo) => {
     return <Todo todo={newTodo} key={newTodo.id}/>

    })
    return(
      <ul>{todoList}</ul>

    ) 
}}


class Todo extends Component {
  render() {
    return( 
      <li className="list-group-item">
            <input type="checkbox" value='on' />
            <label>{this.props.todo}</label>
      </li>
    )
  }

}


export default App;
