import React, { Component } from 'react';


class Input extends Component {
    render() {
        return(
            <form>
            <div className="input-group">
                <span className="input-group-btn">
                    <button className="btn btn-primary" type="submit" onClick={this.props.addTask} >Add</button>
                </span>
                <input className="form-control" onChange={this.props.updateTaskList} value={this.props.task} placeholder="add a todo" />
            </div>
        </form>
        )
    }
  }

export default Input;
